import React, { useState } from "react";
import axios from "axios";
import DataTable from "./DataTable";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);  // Store selected file
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) {
      setMessage("Please select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setMessage("Upload successful!");
      console.log(response.data);  // Log server response
    } catch (error) {
      setMessage("Upload failed");
      console.error("Error uploading file:", error);
    }
  };

  const renderTable = () => {
    if (!fileData || fileData.length === 0) return <p>No data available to display.</p>;

    const keys = Object.keys(fileData[0]);

    return (
      <table border="1" style={{ width: "100%", marginTop: "20px" }}>
        <thead>
          <tr>
            {keys.map((key) => (
              <th key={key}>{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {fileData.map((row, index) => (
            <tr key={index}>
              {keys.map((key) => (
                <td key={key}>{row[key]}</td> // Render row data
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="file-upload">
      <h2>Upload a File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      <p>{message}</p>
      {renderTable()} {/* Display the table after upload */}
    </div>
  );
};



export default FileUpload;
